package org.scraper;

import java.util.List;

import org.scraper.com.fairyloot.community.FairyLootSite;
import org.scraper.com.fairyloot.community.page.BlogPage;
import org.scraper.com.fairyloot.community.page.BookAnnouncementsPage;
import org.scraper.com.fairyloot.community.page.MainPage;
import org.scraper.com.fairyloot.community.page.BookAnnouncementsPage.BlogPost;
import org.scraper.common.GeminiService;

public class App {
    public static void main(String[] args) throws Exception {
        try(FairyLootSite fairylootSite = new FairyLootSite()) {
            MainPage mainPage = fairylootSite.navigate();
            System.out.println(mainPage.title());
            
            BookAnnouncementsPage bookAnnouncementsPage = mainPage.clickBookAnnouncements();
            mainPage.close();

            BlogPost featuredBlogPost = bookAnnouncementsPage.featuredBlogPost();
            System.out.println(featuredBlogPost.title());
            System.out.println(featuredBlogPost.date());

            List<BlogPost> archivedBlogPosts = bookAnnouncementsPage.archivedBlogPosts();
            for(BlogPost post : archivedBlogPosts) {
                System.out.println(post.title());
                System.out.println(post.date());
            }

            BlogPage blogPost = archivedBlogPosts.get(0).navigate();

            GeminiService geminiService = new GeminiService();
            String summary = geminiService.summarize(blogPost.content());
            System.out.println(summary);
        }

        System.exit(0);
    }
}
